		dream.tutorial.8ball, Version=1.0.0.0, Culture=neutral,
PublicKeyToken=null
         &lt;/assembly&gt;
	&lt;type&gt;MindTouch.Dream.Tutorial.EightBallService&lt;/type&gt;
